public class WAP_EVENORODD {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 7;

		if (num % 2 == 0) {

			System.out.println("It is an even number");
		} else {
			System.out.println("It is an odd number");
		}

	}

}
